.cart {
    .product_view_page {
        display: none;
    }
    .header{
            border-bottom:none;
    }
    .home-product .card-body .card-title a {
        letter-spacing: 0;
    }
    .home-product{
        .slick-list.draggable{
            .slick-track{
                margin-left: 0;
            }
        }
    }
    div#swal2-content {
        color: #374866;
        font-family: Roboto;
        font-size: 16px;
        line-height: 22px;
        font-weight: 400;
    }

    .swal2-buttonswrapper{
        .button{

        }
        .swal2-confirm{
                background-color: #665AFF;
                background-color: #665AFF;
                color: white;
                padding: 0;
                width: 100%;
                height: 48px;
                line-height: 48px;
                font-family: Roboto;
                font-size: 16px;
                font-weight: bold;
                max-width: 120px;
                &:hover{
                     background-color:#462AD6
                }

        }
        .swal2-cancel{
                display: inline-block;
                background-color: #FFFFFF;
                border-radius: 22px;
                color: #091832;
                font-family: Roboto;
                font-size: 16px;
                font-weight: 500;
                line-height: 48px;
                height: 48px;
                text-align: center;
                outline: none;
                border: 1px solid #606C84;
                padding: 0 10px;
                transition: 0.3s;
                max-width: 120px;
                width: 100%;
                &:hover{
                    background-color: #091832;
                    border-color: #091832;
                    color: #fff;
                }

        }
    }
    .cart-container {
