			                line-height: 40px;
			                color: #606C84;	
			                font-family: Mont;	
			                font-size: 16px;	
			                font-weight: bold;
			                vertical-align: top;
                            .cart-item-value {
                                color: #606c84;
                            }

                		}
                		.cart-item-quantity{
                			padding: 20px 0;
                			vertical-align: top;

                		}
                		.cart-price-total{
                			width: 15.5%;
                			vertical-align: top;
						    padding: 20px 0;
						    line-height: 40px;
						    text-align: right;
                            position: relative;
                            .cart-item-value  {
                                margin-right: 34px;
                                color: #606c84;
                                font-family: Mont;
                            }
                		}
		               	.form-increment {
		                  border: 1px solid #eee;
		                  margin-left: 0;
		                  margin-right: 0;
		                  border-radius: 3px;
		                  position: relative;
		                  max-width: 70px;
		                  min-width: 70px;

		                  .dec,.inc{
		                    border: none;
		                    width: 24px;
		                    height: 20px;
		                    line-height: 20px;
						    position: absolute;
						    background: transparent;
							color: #606C84;
							font-size: 11px;
                            border-radius: 0;
                            padding: 0;
                            .is-srOnly {
                                display: none;
                            }
                            i {
                                line-height: 20px;
                            }
		                  }
		                  .inc{
		                    right: 0;
		                    top: 0;
		                     border-left: 1px solid #dddde4;
		                    border-bottom: 1px solid #dddde4;
		                  }
		                  .dec{
		                    right: 0;
		                    bottom: 0;
		                    border-left: 1px solid #dddde4;
		                  }
		                  input{
		                        width: 68px;
							    border: none;
							    padding: 0 24px 0 0;
							    height: 38px;
							    line-height: 38px;
							    text-align: center;
							    font-weight: 400;
							   color: #374866;	
							   font-family: Roboto;	
