                                        a{
                                            font-size: 14px;
                                        }
                                    }
                        	}
		                	.cart-total-value{
		                			
		                			.coupon-code-add,.coupon-code-cancel{
		                				color: #665AFF;	
			                			font-family: Roboto;	
			                			font-size: 14px;	
			                			line-height: 20px;	
			                			text-align: right;
			                			text-decoration: underline;
                                        font-style: normal;
                                        &:hover{
                                            background-color: #F1EFFF;
                                        }
		                			}
		                	}
		                }
		                &:nth-child(2){
		                	.sub-total{
		                		padding: 12px 20px 0;
		                		.cart-total-label{
		                			strong{
		                				color: #374866;	
		                				font-family: Roboto;	
		                				font-size: 16px;	
		                				line-height: 28px;
		                				    font-weight: normal;
		                			}
		                		}
		                		.cart-total-value{
		                			color: #374866;	
		                			font-family: Roboto;	
		                			font-size: 16px;	
		                			line-height: 28px;

		                		}

		                	}
		                	.tax-cart{
		                		padding: 0 20px 12px 20px;
		                		.cart-total-label{
		                			strong{
		                				color: #374866;	
		                				font-family: Roboto;	
		                				font-size: 16px;	
		                				line-height: 28px;
		                				    font-weight: normal;
		                			}
		                		}
		                		.cart-total-value{
		                			color: #374866;	
		                			font-family: Roboto;	
		                			font-size: 16px;	
		                			line-height: 28px;
		                		}

		                	}

		                }
		                &:last-child{
