                			color: #374866;	
                			font-family: Roboto;	
                			font-size: 16px;	
                			line-height: 22px;
                			margin: 0;
                			a{
            					color: #374866;	
            					font-family: Roboto;
            					font-size: 16px;
            					font-weight: 700;
            					text-decoration: none;
                			}
                		}
                	}
                }
                .side-bar{
                	padding: 24px 24px 0 24px;
                	margin-top: 16px;
                	p{
                		margin: 0 0 6px 0;
                		strong{
                			color: #091832;	
                			font-family: Roboto;	
                			font-size: 16px;	
                			font-weight: 500;	
                			line-height: 20px;
                		}
                	}
                	ul{
                		list-style-type: none;
                		margin: 0;
                		li{
                			padding-bottom: 21px;
                			padding-top: 16px;
                			&:first-child{
                				padding-top: 0px;

                			}

    						border-bottom: 1px solid #EFF0F2;
    						&:last-child{
    							border-bottom: none;
    						}
                			>span{
                				margin-bottom: 10px;
    							display: block;
                			}
                			p{
                				color: #374866;	
                				font-family: Roboto;	
                				font-size: 16px;	
                				line-height: 20px;
                				margin: 0;
                			}
                		}
                	}
                }
