                width: calc(100% - 392px);
                float: left;
                .custom-cart-products {
                    .card {
                        .card-img-container {
                            max-width: 190px;
                        }
                        .card-body {
                            margin-top: 35px;
                            .card-title {
                                text-align: center;
                                max-width: 200px;
                                margin: 0 auto;
                            }
                            .price-section {
                                margin-top: 16px;
                            }
                        }
                    }
                    .add-cart-home{
                        display: block;
                        margin-top: 8px;
                        height: 48px;
                        line-height: 48px;
                        max-width: 220px;
                        margin: 10px auto 12px;
                        i{
                            margin-right: 8px;
                        }
                    }
                    .card-text{
                        text-align: center;
                        justify-content: center;
                    }
                    .page-heading{
                        color: #091832; 
                        font-family: Mont;  
                        font-size: 24px;    
                        font-weight: bold;  
                        letter-spacing: -0.5px; 
                        line-height: 26px;
                        margin: 0;
                        padding-bottom: 12px;
                        text-align: left;
                    }
                }
                .content-static {
                    margin-bottom: 37px;
                    margin-top: 36px;
                    >h4 {
                        color: #091832; 
                        font-family: Mont;  
                        font-size: 24px;    
                        font-weight: bold;  
                        letter-spacing: -0.5px; 
                        line-height: 26px;
                        padding: 15px 0 13px;
                        border-bottom: 1px solid #EFF0F2;
                        margin: 0;
                    }
                    >div {
                        margin-top: 24px;
                        >div {
                            padding-bottom: 24px;
                            h4 {
                                color: #091832; 
                                font-family: Roboto;   
                                 font-size: 16px;    
                                 font-weight: 500;   
                                 line-height: 20px;
                                 margin: 0;
                                 padding-bottom: 4px;
                                 letter-spacing: 0;
                            }
                            p {
                                color: #374866; 
                                font-family: Roboto;    
                                font-size: 16px;    
                                line-height: 22px;
                                margin: 0;
                                &:nth-of-type(2) {
                                    margin-top: 10px;
                                }
                            }
                        }
            
                    }
                }
            }
        }
    }
}
