.page_contact_form{
    position: relative;

    .page {
        margin: 0;
        background: #FFF;
    }
    .page_contact_form-container {
        max-width: none;
        padding: 0;
        #contact-us-page {
            max-width: 1280px;
            padding: 0 20px;
            width: 100%;
            margin: 0 auto;
            margin-top: 63px;
            &>div {
                float: left;
            }
            .form-left {
                padding-left: 73px;
                width: 50.3%;
                .form {
                    border: 1px solid #EFF0F2;
                    border-radius: 5px;
                    background-color: #FFFFFF;
                    box-shadow: 2px 2px 16px 0 rgba(0,0,0,0.06);
                    padding: 40px 48px 40px;
                    margin-bottom: 0;
                    h4{
                        color: #091832;
                        font-family: Mont;
                        font-size: 24px;
                        font-weight: bold;
                        letter-spacing: -0.5px;
                        line-height: 26px;
                        margin-top: 3px;
                        margin-bottom: 13px;
                    }
                    .form-row {
                        margin: 0 -15px;
                        .form-field {
                            padding: 0 15px;
                            margin-bottom: 15px;
                            .form-label {
                                margin-bottom: 2px;
                            }
                        }
                    }
                }
                #contact_question {
                    max-height: 102px;
                }
                br {
                    display: none;
                }
                .form-actions {
                    margin-top: 24px;
                    text-align: left;
                    .button {
                        padding: 0 45px;
                        height: 40px;
                        margin: 0;
                    }
                }
            }
            .content {
                width: 49.7%;
                display: flex;
                justify-content: space-between;
                padding: 37px 106px 10px 86px;
                div {
                    div {
                        margin-bottom: 23px;
                    }
                    h3 {
                        margin: 0;
                        color: #091832;
                        font-family: Mont;
                        font-size: 24px;
                        font-weight: bold;
                        letter-spacing: -0.5px;
                        line-height: 26px;
                        margin-bottom: 6px;
                    }
                    a {
                        color: #374866;
                        font-family: Roboto;
                        font-size: 16px;
                        line-height: 22px;
                        text-decoration: none;
                        margin-bottom: 23px;
                        display: block;
                    }
                    h4 {
                        color: #606C84;
                        font-family: Mont;
                        font-size: 11px;
                        font-weight: bold;
                        letter-spacing: 0.5px;
                        line-height: 15px;
                        margin: 0;
                        text-transform: uppercase;
                        margin-bottom: 4px;
                    }
                    p {
                        color: #374866;
                        font-family: Roboto;
                        font-size: 16px;
                        line-height: 22px;
                        margin-bottom: 0;
                    }
                }
            }
        }
    }
    .whatusers {
        padding-top: 103px;
        overflow: hidden;
        position: relative;
        &:before {
            position: absolute;
            content: "";
            height: 100%;
            width: 100%;
