.page-cms-custom{
    position: relative;
    .title-contact {
        background-color: #665AFF;
        padding: 144px 24px 218px;
        position: relative;
        max-height: 480px;
        overflow: hidden;
        &:after {
            content: '';
            position: absolute;
            bottom: -16%;
            right: 0;
            height: 30%;
            background-color: #fff;
            width: 100%;
            transform: skewY(-4deg);
            transform-origin: center top;
        }
        h1 {
            color: #FFFFFF;
            font-family: Mont;
            font-size: 52px;
            font-weight: bold;
            letter-spacing: -1px;
            line-height: 56px;
            text-align: center;
            margin-bottom: 6px;
        }
        p {
            width: 816px;
            color: #FFFFFF;
            font-family: Roboto;
            font-size: 20px;
            line-height: 28px;
            text-align: center;
            max-width: 100%;
            margin: 0 auto;
        }
    }
    #contact-us-page {
        max-width: 1280px;
        padding: 0 20px;
        width: 100%;
        margin: 0 auto;
        margin-top: 63px;
        &>div {
            float: left;
        }
        .form-left {
            padding-right: 106px;
            width: 655px;
            .form {
                border: 1px solid #EFF0F2;
                border-radius: 5px;
                background-color: #FFFFFF;
                box-shadow: 2px 2px 16px 0 rgba(0,0,0,0.06);
                padding: 40px 48px 40px;
                margin-bottom: 0;
                h4{
                    color: #091832;
                    font-family: Mont;
                    font-size: 24px;
                    font-weight: bold;
                    letter-spacing: -0.5px;
                    line-height: 26px;
                    margin-top: 3px;
                    margin-bottom: 13px;
                }
                .form-row {
                    margin: 0 -15px;
                    .form-field {
                        padding: 0 15px;
                        margin-bottom: 15px;
                        .form-label {
                            margin-bottom: 2px;
                        }
                    }
                }
            }
            #contact_question {
                max-height: 102px;
            }
            br {
                display: none;
            }
            .form-actions {
                margin-top: 24px;
                text-align: left;
                .button {
                    padding: 0 45px;
                    height: 44px;
                    line-height: 44px;
                    margin: 0;
                }
            }
        }
        .content-right {
            width: calc(100% - 655px);
            display: flex;
            justify-content: space-between;
            padding: 53px 87px 56px 106px;
            div {
                div {
                    margin-bottom: 23px;
                }
                h3 {
                    margin: 0;
                    color: #091832; 
                    font-family: Mont;  
                    font-size: 18px;    
                    font-weight: bold;  
                    line-height: 24px;
                    margin-bottom: 10px;
                    letter-spacing: 0;
                }
                a {
                    color: #374866;
                    font-family: Roboto;
                    font-size: 16px;
                    line-height: 22px;
                    text-decoration: none;
                    margin-bottom: 23px;
                    display: block;
                }
                h4 {
                    color: #606C84;
                    font-family: Mont;
                    font-size: 11px;
                    font-weight: bold;
                    letter-spacing: 0.5px;
                    line-height: 15px;
                    margin: 0;
                    text-transform: uppercase;
                    margin-bottom: 4px;
                }
                p {
                    color: #374866;
                    font-family: Roboto;
                    font-size: 16px;
                    line-height: 22px;
                    
                    margin-bottom: 10px;
                }
            }
        }
    }
    .page {
        margin: 0;
        background: #FBFCFE;
    }
    .page_contact_form-container {
        max-width: none;
        padding: 0;


    }
    .review{
        clear: left;
    }
    .alertBox {
        margin-top: 10px;
        margin-bottom: 30px;
    }
}
