		margin-left: 25px;
		li{
			margin-bottom: 5px;
		}
	}
	ul{
		//color: #485C7D;
		margin-left: 0;
		list-style: none;
		li{
			padding-left: 35px;
			line-height: 22px;
			margin-bottom: 10px;
			position:relative;
			&:before{
				display: inline-block;
				vertical-align: middle;
				content: "â€¢";
				top:1px;
				left: 10px;
				position:absolute;
			}

		}
	}
	h2{
		border-bottom: 1px solid  #EBEBEB;
		text-transform: capitalize;
		padding-bottom:23px;
		margin-bottom: 33px;
		//color: #061B41;
	}
	//content
	// h2 + p,
	// h2 + p +p{
	// 	color: #3F5271;
	// }
	// ul + p{
	// 	margin-top: 15px;
	// 	color: #485C7D;
	// 	margin-bottom: 8px;
	// }

	h3{
		//color: #032769;
		padding-top: 15px;
		margin-bottom: 8px;
		span{
			color: #032769;
		}
	}
	p{
		//margin-bottom: 10px;
	}
	.color_2{
		color: #7D94B0;
	}
	a{
		text-decoration: none;
	}
	a:focus,a:active{
		color: #665AFF;
	}
	
}
