//workplace-health-template
.workplace-health-template{
	padding-bottom:48px;
	.listing_workplace{
		max-width: 1312px;
	}
	.banner-cms{
		padding-top:156px;
	}
	h1{
		margin-top: 0;
	}
	h2{
		text-align: center;	
		margin-top: 50px;
		margin-bottom: 15px;
	}
	ul{
		margin:0;
		padding:0;
		list-style: none;
		&:after{
			display:table;
			clear: both;
			content: "";
		}
		li{
			float: left;
			width: calc(50% - 32px);
			box-sizing: border-box;
			// height: 240px;
			
			border: 1px solid #EFF0F2;
			border-radius: 0 0 20px 20px;
			background-color: #FFFFFF;
			box-shadow: 2px 2px 16px 0 rgba(0,0,0,0.06);
			&:before{
				border-top:6px solid  #665AFF;
				border-top-left-radius:6px;
				border-top-right-radius:6px;
				width: 100%;
				display: block;
				content: " ";
				position:absolute;
				top: 0;
				left: 0;
			}
			margin: 16px;
			padding:43px 39px 39px 39px;
			min-height: 240px;
			position:relative;
			h3{
				margin-top: 0;
				margin-bottom: 10px;
			}
			p{
				margin-bottom: 0;
				padding-bottom:50px;
			}
			a{

				// height: 44px;
				// width: 194px;
				border-radius: 22px;
				background-color: #665AFF;
				text-decoration: none;
				color: #FFFFFF;
				text-align: center;
				padding:12px 22.845px;
				font-size: 16px;
				font-weight: 500;

				position:absolute;
				bottom:39px;
				&:hover{
					background-color: #462AD6;
				}
			}
		}
		li:nth-child(2n+1){
			clear: left;
		}
	}
}
@media (max-width:767px){
	.workplace-health-template{
		padding-bottom:42px;
		.banner-cms{
			min-height: 270px;
			padding-top:34px;
			h1{
				margin-top: 0;
			}
		}
		h2{
			
			margin-bottom: 14px;
		}
		ul{
			li{
				width: 100%;
				margin-left: 0;
				margin-right: 0;
				margin-top: 8px;
				margin-bottom: 8px;
			}
		}
	}
} 
@media (max-width:400px){
