//Page login
.login{
	margin:0 auto;
	max-width: none;
	.body{
		background-color: #FBFCFD;
	}
}

.container.login-container{
	.login.wrapper{
		margin:0;
		.alertBox.alertBox--error{
			margin-top:16px;
			min-height: 48px;
			margin-bottom: 16px;
			height: unset;
			padding: 15px;
		}
		.alertBox.alertBox--success{
			margin-top:16px;
			min-height: 48px;
			margin-bottom: 16px;
			height: unset;
			padding: 15px;
		}
		.login-row{
			display: flex;
			justify-content: center;
			padding-top: 121px;
			padding-bottom: 124px;
			margin-left: 14px;
			margin-top: 0;

			.login-form.form{
				box-sizing: border-box;	
				border: 1px solid #EFF0F2;	
				border-radius: 5px;	
				background-color: #FFFFFF;	
				box-shadow: 2px 2px 16px 0 rgba(0,0,0,0.06);
				margin-top: 0;
				padding: 39px;
				margin-bottom: 0px;
				width: 35.334%;
				.title{
					height: 26px;	
					width: 64px;	
					color: #091832;	
					font-family: Mont;	
					font-size: 24px;	
					font-weight: bold;	
					letter-spacing: -0.5px;	
					line-height: 26px;
					margin-bottom: 19px;
				}
				.form-field.form-field--input.form-field--inputEmail{
					.form-label{
						color: #374866;	
						font-family: Roboto;	
						font-size: 16px;	
						line-height: 22px;
						height: 22px;
						margin:0px;

					}
					.form-input{
						line-height: unset;
					}
					margin-bottom: 16px;
				}
				.form-field.form-field--input.form-field--inputPassword{
					margin-bottom: 0px;
					color: #374866;	
					font-family: Roboto;	
					font-size: 16px;	
					line-height: 22px;
					.form-input{
						line-height: unset;
					}
					label.form-label {
						margin:0px;
					}
				}
				.form-actions{
					height: 44px;
					margin-top: 16px;
					display: flex;
					align-items: center;
					justify-content: space-between;
					&:before,&:after{
						display: none;
					}
					.button.button--primary{
						height: 44px;	
						width: 96px;	
						border-radius: 22px;	
						transition:all 0.3s;
						margin-top: 0;
					}
					.forgot-password{
						margin:0px;
						height: 20px;
						color: #665AFF;	
						font-family: Roboto;	
						font-size: 15px;		
						text-align: right;
						text-decoration: none;
						margin-top:0px;
						&:hover{
							background: none;
						}

						span:hover{
							background-color: #F1EFFF;
							
						}
					}
				}
			}
			.new-customer{
				padding: 39px;
				margin-left: 3.58333333333%;
				box-sizing: border-box;
				border: 1px solid #EFF0F2;
				border-radius: 5px;
				background-color: #FFFFFF;
				box-shadow: 2px 2px 16px 0 rgba(0,0,0,0.06);
				width: 35.334%;
				.panel{

					.panel-header{
						background-color: #FFFFFF;
						padding: 0;
						h2{
							color: #091832;	
							font-family: Mont;	
							font-size: 24px;	
							font-weight: bold;	
							letter-spacing: -0.5px;	
							line-height: 26px;
							margin-top:1px;
							margin-left: -1px;

						}
					}
					.panel-body {
						background-color: #FFFFFF;
						padding: 0;
						margin:0;
						.new-customer-intro{
							margin-top: 17px;
							color: #374866;	
							font-family: Roboto;	
							font-size: 16px;	
							line-height: 22px;
							margin-left: 0px;
							margin-bottom:0px;
						}
						ul{
							margin-top:12px;
							margin-left: 22px;
							margin-bottom: 0px;
							color: #374866;	
							font-family: Roboto;	
							font-size: 16px;	
							line-height: 22px;
							list-style: none;
							li{
								position: relative;
								line-height: 28px;
								padding-left: 13px;
								&:before{
									font-weight: 700;
									position: absolute;
									content: ".";
									left: -12px;
									width: 5px;
									height: 5px;
									top: -15%;


								}
							}

						}
						a{
							padding: 0px;
							margin-top: 56px;
							display: inline-block;
							height: 44px;	
							width: 177px;	
							border-radius: 22px;	
							
							text-align: center;
							button{
								border-radius: 22px;	
								background-color: #665AFF;
								padding: 0px;
								width: 100%;
								transition:all 0.3s;
								&:hover{
									background-color: #462AD6;

								}

							}

						}
					}

				}

			}

		}
	}

}

// Page Login responsive
@media only screen and (max-width: 1200px) {

	.container.login-container{
		.login.wrapper{
			.login-row{
				margin-left: 0px;
				.login-form.form{
					width: 50%;
				}

				.new-customer{
					width: 50%;
				}
			}

		}


	}
}

@media only screen and (min-width: 768px){

