// Page Account 

.account_orderstatus-container,.account_inbox-container,.account_addressbook-container,.editaccount-container,.shippingaddressform-container{
	
	.product_view_page{
		margin:0px;
	}
	width: 100%;
	max-width: 100%;
	padding: 0;
	margin:0;
	.wrapper{
		padding-top: 15px;
		padding-bottom:19px;
		background-color: #FBFCFE;
		border-bottom: 1px solid #EFF0F2;
		.container{
			.breadcrumbs{
				height: 12px;
				line-height: 12px;
				margin-top: 0px;
				.breadcrumb{
					height: 12px;	
					color: #081833;	
					font-family: Roboto;	
					font-size: 12px;	
					line-height: 12px;
					span.breadcrumb-label{
						margin-left: 0px;
					}

					&.is-active{
						height: 12px;
						opacity: 0.6;	
						color: #081833;	
						font-family: Roboto;	
						font-size: 12px;	
						line-height: 12px;
					}
				}
			}

		}

		.page-heading{
			margin:0 auto;
			margin-top:32px;
			margin-bottom: 24px;
			height: 38px;
			color: #091832;	
			font-family: Mont;	
			font-size: 32px;	
			font-weight: bold;	
			letter-spacing: -1px;	
			line-height: 38px;	
			text-align: center;
		}
		.navBar.navBar--sub.navBar--account{
			.title{
				display: none;
			}
			margin:0;
			.navBar-section{
				.navBar-item{
					margin:0;
					padding: 0 10px;

					&:first-child{
						padding-left:0;
					}

					&:last-child{
						padding-right:0;
					}


					a.navBar-action{
						height: 22px;	
						color: #374866;	
						font-family: Roboto;	
						font-size: 16px;	
						line-height: 22px;	

					}

					&.is-active{
						height: 20px;	
						color: #665AFF;	
						font-family: Roboto;	
						font-size: 16px;	
						line-height: 20px;
						border: none;
					}
				}
			}
		}

	}
	.account.account--fixed{
		padding: 0 20px;
		max-width: 1280px;
		width: 100%;
		margin:0 auto;
		.account-head{
			display: none;
		}
		.account-body{
			margin:0 auto;
			width: 65.807%;
			.account-content{
				min-height: 60px;
				box-sizing: border-box;		
				border-radius: 5px;	
				background-color: #FFFFFF;	
				padding: 0 39px;
				margin-top: 48px;
				margin-bottom: 80px;
				
				box-shadow: 2px 2px 16px 0 rgba(0,0,0,0.06);
				border: 1px solid #EFF0F2;
				&.remove-border{
					border:0;
					box-shadow: unset;

				}	

				.account-heading{
					display: none;
				}
				.account-list{
					margin:0px;

					.account-listItem{

						padding: 36px 0 32px;
						border-bottom: 1px solid #EFF0F2;

						&:first-child{
							padding-bottom: 40px;
						}

						.account-product{
							.account-product-figure{

								&:after{
									display: none;
								}
								width: 100px;
								margin-right:24px;
								margin-top: 4px;
								img.account-product-image.lazyautosizes.lazyloaded{
									width: 100px;
									height: 100px;
								}
							}
							.account-product-body{
								.account-orderStatus{
									bottom: -3px;
									h6{
										padding: 0;
										box-sizing: border-box;		
										border: 1px solid #606C84;	
										border-radius: 22px;	
										background-color: #FFFFFF;
										color: #091832;	font-family: Roboto;	
										font-size: 16px;	
										text-align: center;
										text-transform: capitalize;
										height: 44px;	
										width: 187px;
										line-height: 44px;
									}
								}
								.account-product-title{
									margin:0px;
									margin-top:0;
									a{ 
										
										height: 20px;	
										color: #091832;	
										font-family: Roboto;	
										font-size: 15px;	
										font-weight: bold;	
										line-height: 20px;
										text-transform: uppercase;
										padding: 0;
										margin:0;
									}
								}
								.account-product-description{
									margin:0px;
									height: 20px;	
									color: #374866;	
									font-family: Roboto;	
									font-size: 15px;	
									line-height: 20px;
									margin-bottom: 18px;
								}
								.account-product-details{
									.account-product-detail{
										width: auto ;
										padding-right:20px;
										&:last-child{
											padding-left: 12px;
										}
										.account-product-detail-heading{
											height: 20px;	
											color: #091832;	
											font-family: Roboto;	
											font-size: 15px;	
											font-weight: 500;	
											line-height: 20px;
											text-transform: none;
											padding: 0;
											margin:0;
										}
										span{
											height: 20px;	
											color: #374866;	
											font-family: Roboto;	
											font-size: 15px;	
											line-height: 20px;
											display: inline-block;
										}
									}

								}

							}
						}
					}
				}
				.pagination{
					margin-top: 24px;
					margin-bottom: 40px;

					.pagination-list.pagination-list--small{
						margin:0px;
						height: 20px;
						line-height: 20px;
						.pagination-item{
							height: 20px;	
							color: #374866;
							font-family: Roboto;	
							font-size: 15px;	
							line-height: 20px;	
							text-align: center;
						}
						.pagination-item.pagination-item--next{
							top: -10px;
							height: 40px;	
							width: 116px;
							box-sizing: border-box;
							border: 1px solid #091832;
							border-radius: 3px;
							background-color: #FFFFFF;
							transition: all 0.3s;
							.pagination-link{
								width: 100%;
								height: 100%;
								color: #091832;
								font-family: Roboto;
								.icon{
									color: #665AFF;	
									text-align: center;
									svg{
										fill: #665AFF;
									}
								}

							}
							&:hover{
								background-color: #091832;
								.pagination-link{
									color: #FFFFFF;
									font-family: Roboto;
									.icon{
										svg{
											fill:#FED800;
										}
									}

								}
							}
						}
						.pagination-item.pagination-item--previous{
							top: -10px;
							height: 40px;	
							width: 116px;
							box-sizing: border-box;
							border: 1px solid #091832;
							border-radius: 3px;
							background-color: #FFFFFF;
							transition: all 0.3s;
							.pagination-link{
								width: 100%;
