							}
							&:hover{
								background-color: #091832;
								.pagination-link{
									color: #FFFFFF;
									font-family: Roboto;
									.icon{
										svg{
											fill:#FED800;
										}
									}

								}
							}
						}
					}
				}
			}
		}
	}
	
}

