.banner-top{
	position: relative;
	max-height: 632px;
	overflow: hidden;
	background-color: #FBFCFE;
	&:before{
		        content: "";
			    position: absolute;
			    top: 0;
			    left: 0;
			    height: 100%;
			    background: #665aff;
			    width: 79.5%;
			    border-width: 10px;
			    transform: skewX(-47deg);
			    -webkit-transform: skewX(-47deg);
			    -ms-transform: skewX(-47deg);
			    transform: skewX(-47deg);
			    transform-origin: center top;
			    -webkit-transform-origin: center top;
			    -ms-transform-origin: center top;
	}
	&:after{
		        content: "";
			    position: absolute;
			    bottom: -10%;
			    right: 0;
			    height: 19%;
			    background-color: #FBFCFE;
			    width: 100%;
			    border-width: 10px;
			    transform: skewY(-3.45deg);
			    transform: skewY(-3.45deg);
			    transform-origin: center top;
			    -webkit-transform-origin: center top;
		
	}
	.content-text{
		position: absolute;
		top:0;
		z-index: 2;
		width: 100%;
		padding-top: 80px;
		.container{
			>div{
				//max-width: 496px;
				margin-left: 10px;
				h1{
					color: #FFFFFF;	
					font-family: Mont;	
					font-size: 52px;	
					font-weight: bold;	
					letter-spacing: -1px;	
					line-height: 56px;
					@media(min-width: 768px){
						white-space: nowrap;
					}
				}
				p{
					color: #FFFFFF;	
					font-family: Roboto;	
					font-size: 20px;	
					line-height: 28px;
					margin-top: 16px;
					margin-bottom: 0;
					&:not(:first-child){
						margin-top: 3px;
					}
				}
				a{
					margin-top: 23.42px;
				    min-width: 137px;
				    border-radius: 24px;
				    color: #10183A;
				    font-family: Roboto;
				    font-size: 16px;
				    font-weight: 500;
				    background: #fff;
				    height: 48px;
				    line-height: 48px;
				    text-transform: capitalize;
					&:hover{
						color: #fff;	
						background-color: #091832;
					}

				}
			}
		}

	}
}
