sf-product.scss:
body.product{
	overflow: auto;
	.product_view_page{
		margin-bottom: 51px;
	}

}
.productView{
	padding-top:0;
	margin-top:0;
	position:relative;
}
.breadcrumbs_bg{
	padding-bottom:22px;
}
.product_view_page{
	max-width: 100%;
	background:#FFFFFF;
	.product_top_wrap,.breadcrumbs_bg{
		background:#FBFCFE;
		&:before, &:after {
			content: " ";
			display: table;
			clear: left;
		}
	}
	.container{
		padding:0 16px;
		max-width:1272px; 
	}
	.form-field--increments{
		display: none;
	}
	ul{
		list-style: none;
	}
	table{
		max-width:100%;
	}
	.tab-content,
	.product_short_des{
		
		ul{
			margin-left: 0;
			li{
				padding-left: 35px;
				line-height: 22px;
				margin-bottom: 10px;
				position:relative;
				&:before{
					display: inline-block;
					vertical-align: middle;
					content: "â€¢";
					top:1px;
					left: 10px;
					position:absolute;
				}

			}
		}
	}
	.product_short_des{
		> *{
			display: none;
		}
		.productDetailsMove2,
		.productDetailsMove{
			display: block !important;
		}
		.productDetailsMove{
			li{
				display: block;
			}
		}
	}
	
}
.productDetailsMove2,
.productDetailsMove{
	display:none;
}
.productView-image{
	min-height: auto;
	.productView-img-container::after{
		display: none;
	}
	.productView-img-container img{
		position: static;
		width: auto;
		margin: auto;
	}
}

.productView{
	margin-left: 0;
	margin-right:0;
}
.productView-description{
	float: left;
	clear: left;
	.productDetailsMove{
		display: none;
	}
}
.product_right_top,
{
	border: 1px solid #EFF0F2;
	border-radius: 5px;
	background-color: #FFFFFF;
	box-shadow: 2px 2px 16px 0 rgba(0,0,0,0.06);
}

.side-bar {
	box-sizing: border-box;
	border: 1px solid #EFF0F2;
	border-radius: 5px;
	background-color: #FFFFFF;
}
//
.productView-title{
	margin:0;
	color: #091832;
	font-size: 24px;
	font-weight: bold;
	letter-spacing: -0.5px;
	line-height: 26px;
	margin-bottom: 11px;
}

.productView-price{
	color: #1e0078;
	font-family: Mont;
	font-size: 32px;
	font-weight: bold;
	line-height: 32px;
	margin-bottom: 8px;
}
.product._wrap{
	.custom-gst{
		color: #5f697a;
		font-weight: 400;
		font-size: 12px;
	}
	.similar-product{
		.custom-gst{
			display: none;
		}
	}
}
.cart._wrap{
	.custom-gst{
		display: none;
	}
}

.sku-label{
	font-size: 14px;
	font-weight: 500;
	line-height: 14px;
	margin-bottom: 13px;
}
.productView-info-value{
	margin-bottom: 7px;
}
.productView-details{
	.productView-rating{
		padding:18px 24px 6px 24px;
	}
	.productView-price{
		padding:0 24px;
	}
	div.deniver{
		border-radius: 0 0 5px 5px;
		height: 1px;
		background:#EFF0F2;
		content: " ";
		display: block;
	}
	.productView-options{
		padding:0 23px;
		//margin-bottom: 0;
		margin-bottom: 23px;
	}
	.productView-options .form-action.btn-add-to-cart{
		margin-top:7px;
		width: 100%;
		display: block;
		button{
			width: 100%;
			height: 48px;
			border-radius: 24px;
			padding-top: 2px;
			i{
				margin-right: 4px;
				margin-left: 4px;
				font-size: 18px;
			}
		}
	}
	
}
.tab-2,
.tabs-contents-2 {
	display: none;
}
.show{
	display: block;
}
.productView-rating{
	.icon{
		width: 16px;
		height: 14px;
		i{
			vertical-align: top;
		}
	}
}
.productView-reviewLink{
	margin-left: 3px;
	vertical-align: top;
	> a{
		color: #606C84;
		font-size: 12px;
		font-weight: 500;
		line-height: 14px;
		text-decoration: none;
		text-transform: capitalize;
	}
}
.by-area{
	padding:0 24px;
	padding-top:24px;
	padding-bottom:4px;
	i{
		height: 32px;
		width: 32px;
		padding:1px 9px;
		background-color: #F1F0FF;
		color: #665AFF;
		font-size: 14px;
		line-height: 32px;
		text-align: center;
		border-radius:50%;
		margin-right:10px;
		&.fa-file-word{
			padding-left:11px;
			padding-right:10px;
		}
		&.fa-check{
			padding-left:9.5px;
			padding-right:8.5px;
		}
	}
	ul {
		margin-left: 0;
		margin-top:-1px;
		li{
			&:before{
				display: none;
			}
			margin-bottom: 14px;
		}
	}
	&.phone{
		padding-top:18px;
		padding-bottom: 22px;
		background:#FBFCFD;
		border-radius: 0 0 5px 5px;
		p{
			strong{
				font-weight: 500;
				color: #091832;
				line-height: 20px;
			}
			margin-bottom: 3px;
		}
		a.contact_us{
			text-decoration: none;
			margin-top:10px;
			display: block;
			color: #665AFF;
			&:hover{
				max-width: 79px;
			}
		}
	}
}

.productView-details,
.productView-product{
	border-bottom:0;
	padding:0;
	margin-bottom: 0;
}
.position_static{
	position:static;
