/*tab*/
#tab-video{
	padding-top:27px;
	padding-bottom:27px;
}
.video_list{
	position:relative;
	padding:8px;
	&:before,a{
		background: rgba(0,0,0,.2);
		position: absolute;
		left: 8px;
		right: 8px;
		top: 8px;
		bottom: 8px;
		z-index: 1;
		display: block;
		content: " ";

	}

	i{
		color: #FFFFFF;
		font-size: 40px;
		position: absolute;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
		z-index: 9;
	}
	&:after{
		display: table;
		clear: both;
		content: " ";
	}
}
