	border-bottom:1px solid #EFF0F2;
	width: 100%;
	clear: left;
	height: 1px;
	padding-top: 26px;
	margin-bottom: 48px;
	background: #FBFCFE;
}
/*side bar product*/
.product_view_page {
	.side-bar{
		margin-top:24px;
		padding-top:23px;
		p{
			padding-left:24px;
			padding-right:24px;
			strong{
				font-weight: 500;
				color: #091832;
				line-height: 20px;
			}
			margin-bottom: 0;
		}
		ul {
			margin-left: 0;
			margin-right:0;
			border-bottom:1px solid  #EFF0F2;
			margin-bottom: 0;
			li{
				margin-left:24px;
				margin-right:24px;
				border-bottom:1px solid  #EFF0F2;
				padding-top:24px;
				padding-bottom:24px;
				&:before{
					display: none;
				}
				> span{
					display: block;
					line-height: 0;
					.icon{
						vertical-align: top;
						
					}
					i{
						vertical-align: top;
					}
				}
				p{
					padding-left:0;
					line-height: 20px;
					margin-top:6px;
					margin-bottom: 0;
				}

			}
			li:first-child{
				padding-top:15px;
			}
			li:last-child{
				border-bottom:0;
				padding-bottom:18px;
			}
		}
		.cms_2{
			padding-top:28px;
			background:#FBFCFD;

		}
		.fa-video{
			color: #665AFF;
			font-size: 16px;
			line-height: 20px;
			margin-right: 10px;
			+ a{
				color: #374866;
				font-size: 16px;
				font-weight: 500;
				line-height: 20px;
			}

		}
		div.cms_swws{
			border-top:1px solid #E9EAED;
			margin-left: 24px;
			margin-right: 24px;
			padding-bottom:18px;
			padding-top:20px;
			margin-top:15px;
			p{
				padding-left:0;
				padding-right:0;
				line-height: 20px;
				width: 100%;
				margin-bottom: 10px;
			}
		}
	}
	
}

/*end side bar*/
/*layout*/
@media (min-width: 1024px){
	.productView-images,
	.productView-details{
		padding:0;
	}
	.product-left-container,
	.productView-description,
	{
		width: calc(100% - 318px);
		max-width: 848px;
		float: left;
		clear: left;
		padding:0;
		&:after{
			display: table;
			clear: both;
			content: " ";
		}
	}
	.productView-description{
		max-width: 852px;
	}
	.product-left-container + .productView-details,
	.sidebar_desktop{
		width: 318px;
		float: right;
		clear: right;
	}
	.similar-product{
		max-width: 852px;
		
	}
	.product-left-container{
		.productView-images{
			width: 286px;
			margin-bottom: 20px;
		}
		.productView-details{
			width: calc(100% - 286px);
			padding-left: 32px;
		}
	}
}
@media (min-width: 1024px) and (max-width: 1199px){
	.product-left-container,
	.productView-description{	
		padding-right:16px;
	}
}
@media (min-width: 768px) and (max-width: 1023px){
	.product-left-container + .productView-details,.similar-product{
		width: 100%;
		.form-action.btn-add-to-cart button{
			max-width: 270px;
		}
	}
	.productView-description{
		margin-top:20px;
		padding-left:0;
		padding-right:0;
	}
}
/*end layout*/ 


