/*tab mobile*/
@media (max-width: 800px){
	.product_view_page {
		.tabs-contents{
			border:0;
		}
		.tab-title{
			text-align: left;
			font-size: 16px;
			padding: 21px 16px 20px;
			font-weight: 500;
			position: relative;
			&:hover{
				color: #665AFF;
				background:#fff;
			}
			&:after{
				display: inline-block;
