@media (min-width: 768px) {
	.request-a-quote {
		.container {
			max-width: 900px;
			margin: 0 auto;
			margin-bottom: 95px;

			.search-form {
				position: relative;
				width: 256px;
				min-height: 44px;
				max-width: 256px;
				border-radius: 5px;
				background-color: #E9EDF5;
				margin-bottom: 18px;

				#searchInput {
					padding-left: 40px;
					padding-right: 10px;
					width: 256px;
					min-height: 44px;
					max-width: 256px;
					border-radius: 5px;
					background-color: #E9EDF5;
					border: none;
					color: #374866;
					font-family: Roboto;
					font-size: 16px;

					&:focus {
						border: none !important;
						outline: none;
						outline-offset: 0px;
					}
				}

				&:before {

					font-family: "Font Awesome 5 Free";
					font-weight: 900;
					content: "\f002";
					position: absolute;
					font-size: 18px;
					color: #665AFF;
					top: 25%;
					left: 15px;

				}
			}

			.product_listing_quote {
				max-width: 811px;
				border: 1px solid #EFF0F2;
				border-radius: 5px 5px 0 0;
				background-color: #FFFFFF;

				.item {
					position: relative;
					padding-right: 132px;
					border-bottom: 1px solid #EFF0F2;

					.detail {
						padding-bottom: 26px;
						padding-top: 28px;
						border-right: 1px solid #EFF0F2;
						padding-left: 32px;

						h4 {

							letter-spacing: 0.1px;
							margin: 0;
							min-height: 18px;
							color: #091832;
							font-family: Mont;
							font-size: 15px;
							font-weight: 600;
							line-height: 18px;
						}

						a {
							min-height: 20px;
							color: #665AFF;
							font-family: Roboto;
							font-size: 14px;
							font-weight: 500;
							line-height: 20px;
						}

					}

					p {
						position: absolute;
						top: 10%;
						right: 10px;
						width: 110px;
						color: #606C84;
						font-family: Mont;
						font-size: 16px;
						font-weight: bold;
						line-height: 16px;
						text-align: center;
					}

					.button.button-primary.button_add_quote {
						position: absolute;
						top: 40%;
						right: 10px;

					}

					&:last-child {
						border-bottom: 0;

					}

				}
			}

		}


	}
}

