.vex.vex-theme-flat-attack {

	ul {
		display: none;
	}

	.vex-content.qn-request-form-v4 {
		padding: 9px;
		padding-top: 32px;
		background-color: #FFFFFF !important;

		.mui-container-fluid.qn-wrapper {
			#qn-quote-form {
				@media (min-width:768px){
					display: flex !important;
				}

				.qn-form-v4 {
					.mui-textfield.mui--show {
						margin-bottom: 13px !important;
						label {
							min-height: 22px;
							color: #374866;
							font-family: Roboto;
							font-size: 16px;
							line-height: 22px;

							span {
								color: #374866;
							}

							&.error {
								position: relative;
								color: red;
							}

						}

						input {
							margin-top: 9px !important;
							padding: 0 16px;
							box-sizing: border-box;
							height: 48px;
							border: 1px solid #DDDDE4;
							border-radius: 3px;
							background-color: #FFFFFF;
							padding-right: 5px;

							&:focus {
								box-sizing: border-box;
								height: 48px;
								border: 1px solid #DDDDE4;
								border-radius: 3px;
								background-color: #FFFFFF;

							}

							&.invalid {
								border: 1px solid #DDDDE4 !important;
							}

							&.error {
								border: 1px solid red !important;
							}
						}

						.mui--text-caption.mui--text-danger {
							display: none !important;
						}
					}

					.mui-form.mui--show {
						.mui-select {
							margin-bottom:13px !important;
							label {
								min-height: 22px;
								color: #374866;
								font-family: Roboto;
								font-size: 16px;
								line-height: 22px;

							}

							#qn-country {
								margin-top: 9px !important;
								box-sizing: border-box;
								border: 1px solid #DDDDE4;
								border-radius: 3px;
								background-color: #FFFFFF;
								height: 48px;
								padding: 0 16px;
								background-position: 97% 50%;
							}
						}

						.mui-textfield {
							margin-bottom:13px !important;
							label {
								min-height: 22px;
								color: #374866;
								font-family: Roboto;
								font-size: 16px;
								line-height: 22px;

							}

							input {
								margin-top: 9px !important;
								padding: 0 16px;
								box-sizing: border-box;
								height: 48px;
								border: 1px solid #DDDDE4;
								border-radius: 3px;
								background-color: #FFFFFF;

								&:focus {
									box-sizing: border-box;
									height: 48px;
									border: 1px solid #DDDDE4;
									border-radius: 3px;
									background-color: #FFFFFF;

								}
							}

						}
					}

					#qn-locality-fields {
						.mui-select {
							margin-bottom: 13px !important;
							label {
								min-height: 22px;
								color: #374866;
								font-family: Roboto;
								font-size: 16px;
								line-height: 22px;

							}

							#qn-state {
								margin-top: 9px !important;
								box-sizing: border-box;
								border: 1px solid #DDDDE4;
								border-radius: 3px;
								background-color: #FFFFFF;
								height: 48px;
								padding: 0 16px;
								background-position: 97% 50%;
							}
						}
						.mui-textfield{
							margin-bottom:13px !important;
						}
					}


				}

				.qn-product-list-v4 {
					.mui-container-fluid {

						color: #374866;
						font-family: Roboto;
						font-size: 16px;
						line-height: 22px;
						margin-bottom: 15px;
					}

					.qn-product-row {
						position: relative;
						display: flex;
						flex-wrap: wrap;
						border-bottom: 1px solid #EFF0F2;
						&:not(:first-child)
						{
							.mui-col-md-8:first-child{
								margin-top: 30px;
								
							}
							.mui-col-md-4.mui--text-right{
								margin-top: 24px;
							}
						}


						.mui-col-md-8 {
							width: 75%;
							padding: 0 8px;
							line-height: 20px;
							&:first-child{
								margin-bottom: 3px;
								
							}

							a {
								height: 20px;
								color: #665AFF;
								font-family: Roboto;
								font-size: 16px;
								line-height: 20px;
							}

							.mui--text-caption {
								font-family: Roboto;
								font-size: 16px;
								line-height: 20px;
								margin-top: 0px;
								height: 44px;
							}
						}

						.mui-col-md-4 {
							width: 25%;
							padding: 0 8px;

							&.mui--text-right {
								overflow: hidden;
								font-size: 16px;
								font-family: Roboto;
								line-height: 26px;
								width: 25%;
								&+.mui-col-md-8{
									width: 25%;
								}
							}

							&.mui-form--inline {

								display: flex;
								justify-content: flex-end;
								width: 75%;

								.qn-form-qty {
									width: 90px;
									margin-right: 0;
									padding-top: 10px;

									input {

										border-top-left-radius: 3px;
										border-bottom-left-radius: 3px;
										height: 38px;
										border: 1px solid #DDDDE4;
										width: 48px;
										text-align: center !important;

										&.error {
											border: 1px solid #DDDDE4 !important;
											color: black !important;
										}
									}

									label.error {
										display: none !important;
									}

									.up {
										cursor: pointer;
										display: inline-block;
										position: absolute;
										border: 1px solid #dddde4;
										border-top-right-radius: 3px;
										border-left: none;
										border-bottom: none;
										width: 22px;
										height: 21px;
										line-height: 15px;
										text-align: center;
										top: 10px;
										right: 21px;

										i {
											display: inline-block !important;
											width: 9.63px;
											height: 11px;
											font-size: 11px;
											color: #606C84;
											font-weight: 500;
										}

									}

									.down {
										cursor: pointer;
										display: inline-block;
										position: absolute;
										border: 1px solid #dddde4;
										border-bottom-right-radius: 3px;
										border-left: none;
										width: 22px;
										height: 20px;
										line-height: 17px;
										text-align: center;
										top: 28px;
										right: 21px;

										i {
											display: inline-block !important;
											width: 9.63px;
											height: 11px;
											font-size: 11px;
											display: block;
											color: #606C84;
											font-weight: 400;
										}

									}

								}

								button {
									width: auto !important;
									height: auto !important;
									padding: 0 !important;
									margin: 0;
									border-radius: 0;
									background: none !important;
									box-shadow: none;

									.delete-product {
										font-size: 0px;

										&:before {
											margin-top: 5px;
											font-family: "Font Awesome 5 Free";
											font-weight: 900;
											content: "\f00d";
											width: 24px;
											height: 24px;
											border-radius: 50%;
											background-color: #606C84;
											font-size: 16px;
											display: flex;
											align-items: center;
											justify-content: center;
										}
									}

									.update-product {
										font-size: 0px;

										&:before {
											margin-top: 5px;
											font-family: "Font Awesome 5 Free";
											font-weight: 900;
											content: "\f00c";
											width: 24px;
											height: 24px;
											border-radius: 50%;
											background-color: #606C84;
											font-size: 16px;
											display: flex;
											align-items: center;
											justify-content: center;
										}

									}

								}


							}
						}
					}

				}

				button {
					height: 44px;
					padding: 0 40px;
					width: auto !important;
					display: block;
					border-radius: 22px;
					background-color: #665AFF;
					text-transform: capitalize;
					color: #FFFFFF;
					font-family: Roboto;
					font-size: 16px;
					font-weight: 500;
					clear: both;
					//margin-left: 16px;
					transition: all 0.3s;

					&:hover {
						background-color: #462AD6;
					}



				}

			}

			&+div {
				display: none;
			}

		}

	}

	.vex-close {
		&:before {
			width: 30px !important;
			height: 30px !important;
			font-size: 24px !important;
		}
	}

	.vex-content {
		.vex-dialog-form {
			.vex-dialog-message {
				font-size: 16px;
				line-height: 22px !important;
				color: #374866;
				font-family: Roboto;

			}

			.vex-dialog-buttons {
				display: flex;
				justify-content: center;
				padding-bottom: 16px;

				.vex-first,
				.vex-last {

					text-transform: lowercase !important;
					letter-spacing:0; 
					border-radius: 22px;
					height: 44px;
					line-height: 44px;
					background-color: #665AFF !important;
					color: #FFFFFF;
					font-family: Roboto;
					font-size: 16px;
					font-weight: 500;
					text-align: center;
					padding: 0 40px !important;
					transition: all 0.3s;
					box-shadow: none !important;
					
					&:first-letter{
					   text-transform: uppercase !important;
					}

					&:focus {
						background-color: #665AFF !important;
					}

					&:hover {
						background-color: #462AD6 !important;
						box-shadow: none !important;
					}
				}
                .vex-first{
					width: 132px !important;
					padding: 0 !important;
				}
				.vex-last {
					margin-left: 16px;
					//margin-bottom: 15px !important;
				}

			}
		}
	}

}

