@media (min-width:2000px) {
	.banner-top{
		.only-desktop{
			width: 100%;
		}
		&::after {
		    content: "";
		    position: absolute;
		    bottom: -18%;
		    right: 0;
		    height: 33%;
		    background-color: #FBFCFE;
		    width: 100%;
		    border-width: 10px;
		    transform: skewY(-2.55deg);
		    transform-origin: center top;
		    -webkit-transform-origin: center top;
		}
	}
	.testimonials .banner-top::after {
	    bottom: -18%;
	    right: 0;
	    height: 33%;
	    background-color: 
	    #fff;
	    width: 100%;
	    -webkit-transform: skewY(-2.55deg);
	    transform: skewY(-2.55deg);
	}
	.banner-cms::after {
	    content: '';
	    bottom: -18%;
	    height: 33%;
	    width: 100%;
	    -webkit-transform: skewY(-2.5deg);
	    transform: skewY(-2.5deg);
	    -webkit-transform-origin: center top;
	}

	.about-us .banner-cms::after {
	    bottom: -18.5%;
	    height: 33%;
	   transform: skewY(-2.5deg);
	}
	.banner-top-blog{
		background-position: bottom;
		background-position: bottom 0 left 0;
		background-size: cover;
	}
	.page.custom-faq-page .page-content .banner-cms::after {
	    height: 33%;
	    bottom: -18%;
	    -webkit-transform: skewY(-5deg);
	    transform: skewY(-2.55deg);
	    z-index: -9;
	}
}
