}
body{
  color: #374866;
  font-family: Roboto;
  font-size: 16px;
  line-height: 22px;
  padding-top: 0px;
}
.body {
  margin-top: 0px;
  margin-bottom: 0px;
}
.container{
  max-width:  1280px;
  padding:0 20px;
  width:100%;
  margin:0 auto;
}
.pagination {
  .pagination-list {
    .pagination-item {
      margin-left: 0;
      a {
        line-height: 40px;
        width: 40px;
        height: 40px;
        padding: 0;
        border-radius: 50%;
        font-family: Roboto;
        font-size: 16px;
        font-weight: 400;
        text-align: center;
        color: #374866;
        &:hover {
          color: #374866;
        }
      }
      &.pagination-item--current {
        border: none;
        border-radius: none;
        a {
          background-color: #665AFF;
          color: #fff;
          font-weight: 500;
          &:hover {
            color: #fff;
          }
        }
      }
      &.pagination-item--previous {
        float: none;
        a {
          color: #665AFF;
          font-size: 13px;
          i {
            font-weight: 600;
          }
          &:hover {
            color: #665AFF;
          }
        }
      }
      &.pagination-item--next {
        float: none;
        a {
          color: #665AFF;
          font-size: 13px;
          i {
            font-weight: 600;
          }
          &:hover {
            color: #665AFF;
          }
        }
      }
    }
  }
}
.alertBox {
  border-radius: 3px;
  border:none;
  padding: 16px;
  color: #FFFFFF;
  font-family: "Helvetica Neue";
  font-size: 13px;
  font-weight: bold;
  line-height: 16px;
  display: flex;
  align-items: center;
  img {
    margin-right: 10px;
  }
  span {
    text-align: left;
  }
  i {
    color: #FFFFFF;
    font-size: 18px;
    font-weight: 300;
    line-height: 18px;
    margin-left: auto;
    cursor: pointer;
  }
  &.alertBox--success{
    background-color: #665AFF;
  }
  &.alertBox--error{
    background-color: #E74C3C;
  }
  &.alertBox--info{
    background-color: #091832;
  }
}
.hinden{
  display: none !important;
}
.button, .button-standard, .btn {
  background-color: #0AAD6A;
  border-radius: 22px;
  color: #FFFFFF;
  font-family: Roboto;
  font-size: 16px;
  font-weight: 500;
  line-height: 44px;
  height: 44px;
  text-align: center;
  outline:none;
  border: none;
  padding: 0 10px;
  transition: 0.3s;
  outline: none;
  display: inline-block;
  text-decoration: none;
  &:hover, &:focus{
    background-color: #249349;
    color: #FFFFFF;
    transition: 0.3s;
  }
  &:active{
    background-color: #249349;
    border: none;
    color: #FFFFFF;
    transition: 0.3s;
  }
}
.button-primary, .btn-primary {
  background-color: #665AFF;
  border-radius: 22px;
  color: #FFFFFF;
  font-family: Roboto;
  font-size: 16px;
  font-weight: 500;
  line-height: 44px;
  height: 44px;
  text-align: center;
  outline:none;
  border: none;
  padding: 0 10px;
  transition: 0.3s;
  display: inline-block;
  text-decoration: none;
  &:hover, &:focus{
    background-color: #462AD6;
    transition: 0.3s;
    color: #FFFFFF;
  }
  &:active{
    background-color: #462AD6;
    transition: 0.3s;
    color: #FFFFFF;
  }
}
.button-hollow, .btn-hollow {
  background-color: #FFFFFF;
  border-radius: 22px;
  color: #091832;
  font-family: Roboto;
  font-size: 16px;
  font-weight: 500;
  line-height: 42px;
  height: 44px;
  text-align: center;
  outline:none;
  border: 1px solid #606C84;
  padding: 0 10px;
  transition: 0.3s;
  display: inline-block;
  text-decoration: none;
  &:hover, &:focus{
    background-color: #091832;
    border: 1px solid #091832;
    transition: 0.3s;
    color: #FFFFFF;
  }
  &:active{
    background-color: #091832;
    border: 1px solid #091832;
    transition: 0.3s;
    color: #FFFFFF;
  }
}
.button-disable, .btn-disable {
  background-color: #EEEEEE;
  color: #AAAAAA;
}
.form-field{
  margin:0 0 24px;
}
form {
  .form-label {
    &.error {
    }
  }
  .form-label:not(.checkbox_radio):not(.error) {
    color: #374866;
    font-family: Roboto;
    font-size: 16px;
    line-height: 22px;
    margin-bottom: 3px;
    &.error-label{
      color: #DF0C0C;
    }
  }
}
input[type="text"], input[type="email"], input[type="password"], input[type="number"], input[type="tel"], input[type="date"], .form-input {
  padding: 12px 14px 14px;
  -webkit-appearance: none;
  border: 1px solid #DDDDE4;
  border-radius: 3px;
  background-color: #FFFFFF;
  color: #374866;
  font-family: Roboto;
  font-size: 16px;
  line-height: 22px;
  outline: none;
  height: 48px;
  font-weight: 400;
  margin:0 !important;
  transition: none;
  &.error{
    border: 1px solid #FF0000 !important;
    color: #FF0000 !important;
  }
  &:focus{
    &.error {
      border: 1px solid #FF0000;
    }
    border: 1px solid #fff;
    box-shadow: 4px 4px 12px 0 rgba(0,0,0,0.12);
  }
  &::-webkit-input-placeholder { /* Edge */
    color: #939393;
  }
  
  &:-ms-input-placeholder { /* Internet Explorer 10-11 */
    color: #939393;
  }
  &::placeholder {
    color: #939393;
  }
  & + .error {
    color: #FF0000;
    font-family: Roboto;
    font-size: 16px;
    line-height: 16px;
    margin-top: 2px;
    margin-bottom: 0px;
    display: block;
  }
}
select, .form-select {
  padding: 13px 14px;
  -webkit-appearance: none;
  border: 1px solid #DDDDE4;
  border-radius: 3px;
  background-color: #FFFFFF;
  color: #374866;
  font-family: Roboto;
  font-size: 16px;
  outline: none;
  height: 48px;
  margin:0 !important;
  position: relative;
  background-image: url('../img/icon-dropdown.jpg');
  background-size: 12px;
  font-weight: 400;
    background-position: center right 14px;
    background-repeat: no-repeat;
  &:focus{
    border: 1px solid #CCCCCC;
    background-image: url('../img/icon-up.jpg');
  }
  &.error{
    border: 1px solid #FF0000;
    background-image: url('../img/select-error.jpg');
  }
}
textarea {
  padding: 13px 14px;
  -webkit-appearance: none;
  border: 1px solid #DDDDE4;
  border-radius: 3px;
  background-color: #FFFFFF;
  color: #374866;
  font-family: Roboto;
  font-size: 16px;
  line-height: 46px;
  outline: none;
  &:focus{
    border: 1px solid #fff;
    box-shadow: 4px 4px 12px 0 rgba(0,0,0,0.12);
  }
}
.form-checkbox, input[type=checkbox], .form-radio, input[type=checkbox] {
  & + .form-label {
    padding-left: 28px;
    color: #374866;
    font-family: Roboto;
    font-size: 16px;
    line-height: 20px;
    font-weight: 400;
    text-transform: none;
    position: relative;
    transform: none;
    transition:none;
    background-size:20px;
  }
}
.form-checkbox, input[type=checkbox] {
  display:none;
  & + .form-label {
      &:before{
          content: "";
          display: block;
          position: absolute;
          top: 0;
          border-radius: 3px;
          left: 0;
          width: 20px;
          height: 20px;
          line-height: 18px;
          border: 1px solid #DDDDE4;
          background-color: #FFFFFF;
          z-index: 1;
          font-weight: 900;
          font-family: "Font Awesome 5 Pro";
          font-size: 12px;
          text-align: center;
          box-sizing: border-box;
      }
  }
  &:checked + .form-label {
      &:before{
          content: "\f00c";
          background-color: #665AFF;
          border-color: #665AFF;
          color: #fff;
      }
  }
}
.form-radio, input[type=radio] {
  display: none;
  & + .form-label {
    &:before{
        content: "";
        display: block;
        position: absolute;
        top: 0;
        left: 0;
        width: 20px;
        height: 20px;
        line-height: 18px;
        border: 1px solid #E4E4E4;
        background-color: #FFFFFF;
        z-index: 1;
        font-size: 10px;
        text-align: center;
        border-radius: 50%;
    }
    &:after{
        content: "";
        display: block;
        position: absolute;
        top: 6px;
        left: 6px;
        width: 8px;
        height: 8px;
        border-radius: 50%;
        z-index: 2;
        background-color: transparent;
        background-color: #fff;
        opacity: 0;
        visibility: hidden;
    }
}
&:checked + .form-label {
    &:before{
        border-color: #665AFF;
        background-color: #665AFF;
    }
    &:after{
        opacity: 1;
        visibility: visible;
        transform: none;
    }
}
}
.form-field--error {
  .form-input, .form-select, .form-checkbox + .form-label::before, .form-radio + .form-label::before, .form-prefixPostfix-label{
    border-color: #FF0000;
    color: #FF0000;
  }
  .form-inlineMessage{
    color: #FF0000;
    font-family: Roboto;
    font-size: 16px;
    line-height: 22px;
    margin-top: 2px;
    margin-bottom: 0px;
    &:before{
      display: none;
    }
  }
}
.form-field--success {
  .form-input, .form-select, .form-checkbox + .form-label::before, .form-radio + .form-label::before, .form-prefixPostfix-label{
    border: 1px solid #fff;
    box-shadow: 4px 4px 12px 0 rgba(0,0,0,0.12);
  }
}
.breadcrumbs-container {
  margin-bottom: 0;
  margin-top: 15px;
  .breadcrumbs {
    text-align: left;
    margin-bottom: 0;
    margin-top: 0;
    display: block;
    .breadcrumb {
      color: #081833;
      font-family: Roboto;
      font-size: 12px;
      line-height: 12px;
      &:first-child:before {
        display: none;
      }
      &:before {
        content: "\f054";
        font-family: "Font Awesome 5 Pro";
        font-weight: 400;
        font-size: 9px;
        line-height: 10px;
        color: #665AFF;
        margin: 0 8px;
        position: static;
      }
      &.is-active {
        .breadcrumb-label {
          margin-left: -4px;
          opacity: 0.6;
          color: #081833;
          font-family: Roboto;
          font-size: 12px;
          line-height: 12px;
