// MEGA menu
.navPages-container {
  border-top: 1px solid #EFF0F2;
  .container {
    padding: 0 16px;
  }
  .navPages {
    display: flex;
    align-items: center;
    justify-content: space-between;
    max-width: none;
    &:before, &:after {
      display: none;
    }
    .navPages-list {
      .navPages-item {
        &.not-child {
          &:hover {
            &>.navPages-action {
              &:after {
                display: none;
              }
            }
          }
        }
        &:first-of-type {
          .navPages-action {
            padding-left: 5px;
          }
        }
        &:hover {
          &>.navPages-action {
            color: #665AFF;
            background-color: transparent;
            position: relative;
            &:after {
              position: absolute;
              bottom: -1px;
              z-index: 1;
              left: calc(50% - 9px);
              content: "";
              display: block;
              width: 0px;
              height: 0px;
              border-left: 10px solid transparent;
              border-right: 10px solid transparent;
              border-bottom: 8px solid #fff;
              transform: translateX(-50%);
            }
            i {
              color: #665AFF;
              &.fa-chevron-up {
                display: inline;
              }
              &.fa-chevron-down {
                display: none;
              } 
            }
          }
          .navPage-subMenu {
            display: flex;
            align-items: flex-start;
          }
        }
        .navPages-action {
          &:hover {
            background-color: transparent;
          }
          padding: 15px 19.6px;
          text-transform: none;
          color: #091832;
          font-family: Roboto;
          font-size: 14px;
          font-weight: 500;
          line-height: 20px;
          position: relative;
          &.is-open {
            background-color: transparent;
          }
          &>span, .fa-chevron-right {
            display: none;
          }
          i {
            font-size: 11px;
            margin-left: 1px;
            color: #606C84;
            position: absolute;
            right: 5px;
            transform: translateY(-50%);
            top: 50%;
            line-height: 20px;
            &.fa-chevron-up {
              display: none;
            } 
          }
        }
        .navPage-subMenu {
          box-shadow: 4px 4px 12px 0 rgba(0,0,0,0.2);
          width: auto;
          left: -12px;
          border-radius: 10px;
          background-color: #fff;
          padding: 33px 40px 26px !important;
          align-items: end;
          justify-content: space-between;
