    padding-top: 17px;
    padding-bottom: 0px;
    h3 {
      text-align: left;
      padding: 0 16px;
      line-height: 22px;
      font-size: 18px;
      margin-bottom: 14px;
    }
    .container {
      &>div {
        width: 100%;
        padding-left: 0 !important;
        margin-bottom: 24px;
        flex-direction: row;
        text-align: left;
        align-items: flex-start;
        a {
          margin-right: 24px;
        }
      }
    }
  }
  .footer {
    padding-top: 21px;
    &>.container {
      display: flex;
      padding: 0;
      flex-direction: column;
      max-width: none;
      .logo {
        display: none;
      }
      .block-footer {
        text-align: left;
        padding-left: 0 !important;
        width: 100% !important;
        h3 {
          position: relative;
          padding: 20px 16px;
          margin-bottom: 0;
          border-top: 1px solid #EFF0F2;
          i {          
            position: absolute;
            right: 16px;
            color: #606C84;
            font-size: 12px;
            line-height: 10px;
            top: 50%;
            transform: translateY(-50%);
          }
          &.active {
            .fa-chevron-down {
              display: none;
            }
            .fa-chevron-up {
              display: inline;
            }
          }
          .fa-chevron-down {
            display: inline;
          }
        }
        ul {
          display: none;
          padding: 0 16px;
        }
        &.block-1 {
          order: 2;
        }
        &.block-2 {
          order: 3;
          max-width: none;
        }
        &.block-3 {
          order: 4;
          max-width: none;
        }
        &.block-4 {
          padding-top: 0;
          order: 1;
          width: 100%;
          margin-bottom: 26px;
          max-width: none;
          text-align: left;
          padding: 0 16px !important;
          h3 {
            margin-bottom: 5px;
            padding: 0;
            border: none;
          }
          p {
            margin-top: 0;
            margin-bottom: 13px;
          }
          h3,p {
            margin-left: 0px;
          }          
          .form {
            margin-bottom: 0;
          }
          .form-field .form-prefixPostfix .button {
            width: auto;
            margin-top: 0px;
          }
        }
      }
    }
  }
  .navUser-item--cart .dropdown-menu .previewCart .previewCartAction {
    margin: 0;
  }
