        .cm-subscribe-lists{
        	list-style: none;
        	margin: 0;
        }
        .cm-subscribe-lists li{
            cursor: pointer;
            padding-left: 28px;
            list-style: none;
        	color: #374866;
            font-family: Roboto;
            font-size: 16px;
            line-height: 22px;
            margin-bottom: 5px;
            font-weight: 400;
            text-transform: none;
            position: relative;
            transform: none;
            transition: none;
        }
        .cm-subscribe-lists li:before{
        	content: "";
            display: block;
            position: absolute;
            top: 0;
            border-radius: 3px;
            left: 0;
            width: 20px;
            height: 20px;
            line-height: 18px;
            border: 1px solid #DDDDE4;
            background-color: #FFF;
            z-index: 1;
            font-weight: 900;
            font-family: "Font Awesome 5 Pro";
            font-size: 12px;
            text-align: center;
            box-sizing: border-box;
        }
        .cm-subscribe-lists li.active:before{
