<script type="text/javascript">
    window.language = {{{langJson 'optimized_checkout'}}};
    $(document).on("click",'#checkout-app .layout-main .checkout-steps .checkout-step.checkout-step--billing .checkout-view-content .checkout-form fieldset #billingAddresses .dropdown-toggle--select',function() {
        if($('.dropdown--select').find('.dropdownMenu').length !== 0) {
            $(this).addClass('down');
        } else {
            $(this).removeClass('down');
        }
    });
    $(document).on("click",".alertBox.alertBox--error .alertBox-icon",function() {
    	$(this).parent().hide();
    });
</script>

